package dev.sunnat629.openai_android.networksimport io.ktor.client.HttpClientimport io.ktor.client.engine.android.Androidimport io.ktor.client.plugins.DefaultRequestimport io.ktor.client.plugins.contentnegotiation.ContentNegotiationimport io.ktor.client.request.headerimport io.ktor.http.ContentTypeimport io.ktor.http.HttpHeadersimport io.ktor.http.contentTypeimport io.ktor.serialization.kotlinx.json.jsonimport kotlinx.serialization.json.Jsonobject KTorNetwork {    fun provideHttpClient(): HttpClient {        return HttpClient(Android) { // Use `CIO`, `OkHttp`, or another engine suitable for your platform.            // Install ContentNegotiation feature with KotlinxSerialization            install(ContentNegotiation) {                json(Json {                    // Configuration for the Json instance, e.g.:                    isLenient = true                    ignoreUnknownKeys = true                })            }            // Setup DefaultRequest to add headers to every request            install(DefaultRequest) {                header(HttpHeaders.Authorization, "Bearer YOUR_API_KEY")                contentType(ContentType.Application.Json)            }            // Add other configurations and features as needed.        }    }}